// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

/// @title Red Envelope contract that lets users create and grab time-limited packets
/// @notice Designed for The Graph indexing: all state-changing actions emit events
contract RedEnvelope {
    struct Envelope {
        address creator;
        uint256 totalAmount;
        uint256 remainingAmount;
        uint8 totalSlots;
        uint8 remainingSlots;
        bool equalShare;
        uint64 createdAt;
        bool reclaimed;
    }

    uint256 public constant CLAIM_WINDOW = 30 minutes;
    uint8 public constant MIN_SLOTS = 3;
    uint8 public constant MAX_SLOTS = 10;
    uint256 private constant MIN_PER_CLAIM = 1 wei;

    uint256 private _nextId = 1;
    bool private _locked;

    mapping(uint256 => Envelope) public envelopes;
    mapping(uint256 => mapping(address => bool)) public hasClaimed;

    event EnvelopeCreated(
        uint256 indexed envelopeId,
        address indexed creator,
        uint256 amount,
        uint8 totalSlots,
        bool equalShare,
        uint256 createdAt
    );

    event EnvelopeClaimed(
        uint256 indexed envelopeId,
        address indexed claimer,
        uint256 amount,
        uint8 remainingSlots,
        uint256 remainingAmount
    );

    event EnvelopeReclaimed(
        uint256 indexed envelopeId,
        address indexed creator,
        uint256 amount
    );

    //  claim / reclaim 
    modifier nonReentrant() {
        require(!_locked, "REENTRANCY_GUARD");
        _locked = true;
        _;
        _locked = false;
    }

    /// @notice Create a new red envelope
    /// @param totalSlots Number of grabs allowed, between MIN_SLOTS and MAX_SLOTS (inclusive)
    /// @param equalShare If true, each claim is equal; otherwise, random within remaining balance
    /// @return envelopeId The new envelope id
    function createEnvelope(uint8 totalSlots, bool equalShare)
        external
        payable
        returns (uint256 envelopeId)
    {
        require(totalSlots >= MIN_SLOTS && totalSlots <= MAX_SLOTS, "INVALID_SLOT_COUNT");
        require(msg.value >= totalSlots * MIN_PER_CLAIM, "INSUFFICIENT_FUNDS");

        envelopeId = _nextId;

        unchecked {
            _nextId = envelopeId + 1;
        }

        envelopes[envelopeId] = Envelope({
            creator: msg.sender,
            totalAmount: msg.value,
            remainingAmount: msg.value,
            totalSlots: totalSlots,
            remainingSlots: totalSlots,
            equalShare: equalShare,
            createdAt: uint64(block.timestamp),
            reclaimed: false
        });

        emit EnvelopeCreated(
            envelopeId,
            msg.sender,
            msg.value,
            totalSlots,
            equalShare,
            block.timestamp
        );
    }

    /// @notice Claim from a red envelope by id
    /// @param envelopeId The id of the envelope to claim
    function claim(uint256 envelopeId) external nonReentrant {
        Envelope storage env = envelopes[envelopeId];
        require(env.creator != address(0), "ENVELOPE_NOT_FOUND");
        require(!_isExpired(env), "ENVELOPE_EXPIRED");
        uint8 remainingSlots = env.remainingSlots;
        require(remainingSlots > 0, "NO_REMAINING_SLOTS");
        require(!hasClaimed[envelopeId][msg.sender], "ALREADY_CLAIMED");

        uint256 remainingAmount = env.remainingAmount;
        bool equalShare = env.equalShare;
        uint8 totalSlots = env.totalSlots;
        uint256 totalAmount = env.totalAmount;

        uint256 payout = _calculatePayout(
            remainingSlots,
            remainingAmount,
            equalShare,
            totalSlots,
            totalAmount,
            envelopeId
        );

        hasClaimed[envelopeId][msg.sender] = true;
        unchecked {
            env.remainingSlots = remainingSlots - 1;
            env.remainingAmount = remainingAmount - payout;
        }

        (bool success, ) = msg.sender.call{value: payout}("");
        require(success, "TRANSFER_FAILED");

        emit EnvelopeClaimed(envelopeId, msg.sender, payout, env.remainingSlots, env.remainingAmount);
    }

    /// @notice Reclaim leftover funds after expiration
    /// @param envelopeId The id of the envelope
    function reclaimExpired(uint256 envelopeId) external nonReentrant {
        Envelope storage env = envelopes[envelopeId];
        require(env.creator != address(0), "ENVELOPE_NOT_FOUND");
        require(msg.sender == env.creator, "ONLY_CREATOR");
        require(_isExpired(env), "NOT_EXPIRED");
        require(!env.reclaimed, "ALREADY_RECLAIMED");
        require(env.remainingAmount > 0, "NOTHING_TO_RECLAIM");

        uint256 payout = env.remainingAmount;
        env.remainingAmount = 0;
        env.remainingSlots = 0;
        env.reclaimed = true;

        (bool success, ) = msg.sender.call{value: payout}("");
        require(success, "TRANSFER_FAILED");

        emit EnvelopeReclaimed(envelopeId, msg.sender, payout);
    }

    /// @notice Read envelope details for off-chain indexing
    function getEnvelope(uint256 envelopeId)
        external
        view
        returns (
            address creator,
            uint256 totalAmount,
            uint256 remainingAmount,
            uint8 totalSlots,
            uint8 remainingSlots,
            bool equalShare,
            uint64 createdAt,
            bool reclaimed,
            bool expired
        )
    {
        Envelope storage env = envelopes[envelopeId];
        require(env.creator != address(0), "ENVELOPE_NOT_FOUND");

        creator = env.creator;
        totalAmount = env.totalAmount;
        remainingAmount = env.remainingAmount;
        totalSlots = env.totalSlots;
        remainingSlots = env.remainingSlots;
        equalShare = env.equalShare;
        createdAt = env.createdAt;
        reclaimed = env.reclaimed;
        expired = _isExpired(env);
    }

    /// @notice Check if a user already claimed an envelope
    function claimed(uint256 envelopeId, address user) external view returns (bool) {
        return hasClaimed[envelopeId][user];
    }

    function _calculatePayout(
        uint8 remainingSlots,
        uint256 remainingAmount,
        bool equalShare,
        uint8 totalSlots,
        uint256 totalAmount,
        uint256 envelopeId
    ) private view returns (uint256) {
        // 
        if (remainingSlots == 1) {
            return remainingAmount;
        }

        if (equalShare) {
            // /
            uint256 baseShare = totalAmount / totalSlots;
            // In case of tiny rounding errors, fall back to remainingAmount
            if (baseShare == 0 || baseShare > remainingAmount) {
                return remainingAmount;
            }
            return baseShare;
        }

        //  gas
        //  MIN_PER_CLAIM
        uint256 maxPayout = remainingAmount - (uint256(remainingSlots - 1) * MIN_PER_CLAIM);
        uint256 randomness = uint256(
            keccak256(abi.encodePacked(block.timestamp, msg.sender, envelopeId, remainingAmount))
        );
        uint256 spread = maxPayout - MIN_PER_CLAIM;
        return MIN_PER_CLAIM + (randomness % spread);
    }

    function _isExpired(Envelope storage env) private view returns (bool) {
        return block.timestamp > env.createdAt + CLAIM_WINDOW;
    }

    receive() external payable {
        revert("DIRECT_DEPOSIT_NOT_ALLOWED");
    }

    fallback() external payable {
        revert("DIRECT_DEPOSIT_NOT_ALLOWED");
    }
}

